import time
import sys
sys.path.append('../')

def count_words(text):
    word_count = {}
    words = text.split()
    for word in words:
        count = 0
        for w in words:
            if word == w:
                count += 1
        word_count[word] = count
    return word_count

def fast_count_words(text):
    word_count = {}
    words = text.split()
    for word in words:
        word_count[word] = word_count.get(word, 0) + 1
    return word_count

